(function(){function n(n){return`
<li id="note_${n.Id}" class="d-flex flex-column mt-1 ml-1 mb-3">
    <div class="chat-body white p-3 z-depth-1">
        <div class="header">
            <button type="button" class="deleteNote close" aria-label="Close" data-noteid="${n.Id}">
                <span aria-hidden="true">×</span>
            </button>
            <strong class="primary-font">Σημείωση</strong>
            <small class="pull-right text-muted"><i class="far fa-clock"></i> ${moment(n.CreatedAt).locale("el").format("LL")}</small>
        </div>
        <hr class="w-100">
        <p class="mb-0">
            ${n.Note}
        </p>
    </div>
</li> `}$(function(){$(".chat-message").hide();$(".noteHeader").click(function(){$(".chat-message").show();$(".friend-list").find("li").each(function(){$(this).removeClass("gradient-new-york").addClass("grey").addClass("lighten-3")});$(this).closest("li").removeClass("grey").removeClass("lighten-3").addClass("gradient-new-york");$(".chatList").find("li").each(function(){$(this).remove()});let t=$(this).data("noteid"),i=$(this).data("postid");$.ajax({url:`${domainUrl}/api/GetNotes/${t}`,method:"GET",dataType:"text"}).done(function(t){var r=JSON.parse(t);$.each(r.AspNetUserNotesDetails,function(t,i){$(".chatList").append(n(i))});$(`.sendButton[type="button"]`).data("noteid",i);$(".deleteNote").click(function(){let n=$(this).data("noteid");$.ajax({url:`${domainUrl}/api/DeleteNote/${n}`,method:"POST",dataType:"text"}).done(function(){$(`#note_${n}`).remove()}).fail(function(){})})}).fail(function(){})});$(".deleteNoteGroup").click(function(){let n=$(this).data("postid");$.ajax({url:`${domainUrl}/api/DeleteNotes/${n}`,method:"POST",dataType:"text"}).done(function(){$(`#noteGroup_${n}`).remove()}).fail(function(){})});$(".sendButton").click(function(){let n=$(this).data("noteid");var t={notes:$(`textarea[name="note"]`).val()};$.ajax({type:"POST",url:`${domainUrl}/api/SaveNote/${n}`,contentType:"application/json charset=utf-8",dataType:"json",data:JSON.stringify(t)}).done(function(){$(`.noteHeader[data-postid="${n}"]`).click()}).fail(function(n,t){alert("Request failed: "+t)});$(`textarea[name="note"]`).val("")})})})();